INSERT INTO `questions` (`id`,`skill_id`,`exam_id`,`subject_id`,`position`,`label`,`content`) VALUES (10802,306,5,24,104,'99','<p><strong>Mal secreto</strong><br />\r\nSe a c&oacute;lera que espuma, a dor que mora N&rsquo;alma, e destr&oacute;i cada ilus&atilde;o que nasce,<br />\r\nTudo o que punge, tudo o que devora<br />\r\nO cora&ccedil;&atilde;o, no rosto se estampasse;</p>\r\n\r\n<p><br />\r\nSe se pudesse, o esp&iacute;rito que chora,<br />\r\nVer atrav&eacute;s da m&aacute;scara da face,<br />\r\nQuanta gente, talvez, que inveja agora<br />\r\nNos causa, ent&atilde;o piedade nos causasse!</p>\r\n\r\n<p><br />\r\nQuanta gente que ri, talvez, consigo<br />\r\nGuarda um atroz, rec&ocirc;ndito inimigo,<br />\r\nComo invis&iacute;vel chaga cancerosa!</p>\r\n\r\n<p><br />\r\nQuanta gente que ri, talvez existe,<br />\r\nCuja ventura &uacute;nica consiste<br />\r\nEm parecer aos outros venturosa!<br />\r\n<sub>CORREIA, R. In: PATRIOTA, M. <strong>Para compreender Raimundo Correia</strong>. Bras&iacute;lia: Alhambra, 1995.</sub></p>\r\n\r\n<p><br />\r\nCoerente com a proposta parnasiana de cuidado formal e racionalidade na condu&ccedil;&atilde;o tem&aacute;tica, o soneto de Raimundo Correia reflete sobre a forma como as emo&ccedil;&otilde;es do indiv&iacute;duo s&atilde;o julgadas em sociedade. Na concep&ccedil;&atilde;o do eu l&iacute;rico, esse julgamento revela que&nbsp;</p>\r\n');
