INSERT INTO `questions` (`id`,`skill_id`,`exam_id`,`subject_id`,`position`,`label`,`content`) VALUES (10978,249,5,30,160,'155','<p>Numa escola com 1 200 alunos foi realizada uma&nbsp;pesquisa sobre o conhecimento desses em duas l&iacute;nguas&nbsp;estrangeiras, ingl&ecirc;s e espanhol.<br />\r\nNessa pesquisa constatou-se que 600 alunos falam&nbsp;ingl&ecirc;s, 500 falam espanhol e 300 n&atilde;o falam qualquer um&nbsp;desses idiomas.</p>\r\n\r\n<p><br />\r\nEscolhendo-se um aluno dessa escola ao acaso e&nbsp;sabendo-se que ele n&atilde;o fala ingl&ecirc;s, qual a probabilidade&nbsp;de que esse aluno fale espanhol?</p>\r\n');
