INSERT INTO `questions` (`id`,`skill_id`,`exam_id`,`subject_id`,`position`,`label`,`content`) VALUES (12717,346,6,23,102,'97','<p>&Oacute;ia eu aqui de novo xaxando<br />\r\n&Oacute;ia eu aqui de novo para xaxar<br />\r\n<br />\r\nVou mostrar pr&#39;esses cabras<br />\r\nQue eu ainda dou no couro<br />\r\nIsso &eacute; um desaforo<br />\r\nQue eu n&atilde;o posso levar<br />\r\nQue eu aqui de novo cantando<br />\r\nQue eu aqui de novo xaxando<br />\r\n&Oacute;ia eu aqui de novo mostrando<br />\r\nComo se deve xaxar<br />\r\n<br />\r\nVem c&aacute; morena linda<br />\r\nVestida de chita<br />\r\nVoc&ecirc; &eacute; a mais bonita<br />\r\nDesse meu lugar<br />\r\nVai, chama Maria, chama Luzia<br />\r\nVai, chama Zab&eacute;, chama Raque<br />\r\nDiz que eu tou aqui com alegria</p>\r\n\r\n<p><sub>BARROS, A. <strong>&Oacute;ia eu aqui de novo</strong>. Dispon&iacute;vel em: www.luizluagonzaga.mus.br. Acesso em 5 maio 2013 (fragmento).</sub></p>\r\n\r\n<p><br />\r\nA letra da can&ccedil;&atilde;o de Ant&ocirc;nio de Barros manifesta aspectos do repert&oacute;rio lingu&iacute;stico e cultural do&nbsp;Brasil. O verso que singulariza uma forma caracter&iacute;stica do falar popular regional &eacute;</p>\r\n');
