INSERT INTO `questions` (`id`,`skill_id`,`exam_id`,`subject_id`,`position`,`label`,`content`) VALUES (21217,207,7,23,120,'115','<p><strong>Ya&ocirc;</strong></p>\r\n\r\n<p>Aqui c&oacute; no terreiro</p>\r\n\r\n<p>Pel&uacute; adi&eacute;</p>\r\n\r\n<p>Faz inveja pra gente</p>\r\n\r\n<p>Que n&atilde;o tem mulher</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>No jacut&aacute; de preto velho</p>\r\n\r\n<p>H&aacute; uma festa de ya&ocirc;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;i tem n&ecirc;ga de Ogum</p>\r\n\r\n<p>De Oxal&aacute;, de lemanj&aacute;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mucama de Oxossi &eacute; ca&ccedil;ador</p>\r\n\r\n<p>Ora viva Nan&atilde;</p>\r\n\r\n<p>Nan&atilde; Buruku</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Y&ocirc; y&ocirc;o</p>\r\n\r\n<p>Y&ocirc; y&ocirc;oo</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>No terreiro de preto velho iai&aacute;</p>\r\n\r\n<p>Vamos sarav&aacute; (a quem meu pai?)</p>\r\n\r\n<p>Xang&ocirc;!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><sub>VIANA, G. Ag&oacute;, Pixinguinha! 100 Anos. Som Livre, 1997. </sub></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A can&ccedil;&atilde;o &quot;Ya&ocirc;&quot; foi composta na d&eacute;cada de 1930 por Pixinguinha, em parceria com Gast&atilde;o Viana, que escreveu a letra. O texto mistura o portugu&ecirc;s com o iorub&aacute;, l&iacute;ngua usada por africanos escravizados trazidos para o Brasil. Ao fazer uso do iorub&aacute; nessa composi&ccedil;&atilde;o, o autor</p>\r\n');
